import './MainTable.css'
import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import TableFooter from '@material-ui/core/TableFooter'
import CircularProgress from '@material-ui/core/CircularProgress';
import { Image } from 'antd';
import ImageSwiper from '../../components/Products/ImageSwiper/ImageSwiper';



const useStyles = makeStyles({
    root: {
        width: '100%',
    },
    container: {

    },
    progress: {
        position: 'absolute',
        top: '48%',
        left: '48%'
    },
});

export default function MainTable(props) {
    const [actionDisplay, setActionDisplay] = useState(false)
    const classes = useStyles();
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const columns = props.info.tableHeaders.map(ele => {
        const toCamelCase = (str = '') => {
            return str
                .replace(/[^a-z0-9]/gi, ' ')
                .toLowerCase()
                .split(' ')
                .map((el, ind) => ind === 0 ? el : el[0].toUpperCase() + el.substring(1, el.length))
                .join('');
        };
        return { id: toCamelCase(ele), label: ele }
    })
    const [rows, setRows] = useState([]);
    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    useEffect(() => {
        setRows(() => [...props.data])
        props.info.header !== 'Orders' && props.info.header !== 'Users' ? setActionDisplay(true) : setActionDisplay(false)
    }, [props])
    return (
        <>
            <Typography variant="h4" style={{ marginBottom: 5, color: '#fff' }}>
                {props.info.header}
            </Typography>
            {props.progress ? <CircularProgress className='circular-progress' /> : <Paper className={classes.root}>
                <TableContainer className='table-container'>
                    <Table id="contain" stickyHeader aria-label="sticky table">
                        <TableHead>
                            <TableRow>
                                {columns.map((column) => (
                                    <TableCell
                                        key={column.id}
                                        align={column.align}
                                    >
                                        {column.label}
                                    </TableCell>
                                ))}
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => (
                                <TableRow hover role="checkbox" tabIndex={-1} key={row._id}>
                                    {columns.map((column) => {
                                        const value = row[column.id];
                                        return (
                                            <TableCell key={column.id} align={column.align}>
                                                {column.id === 'photos' ? <ImageSwiper photos={value} /> : column.id === 'photo' ? <Image
                                                    style={{ width: 100 }}
                                                    preview={{ getContainer: '#root', zIndex: 1000000 }}
                                                    src={value}
                                                    fallback="data:image/png;base64,"
                                                /> : value}
                                            </TableCell>
                                        );
                                    })}
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                {actionDisplay ? <TableFooter style={{ display: 'flex' }}>
                    <div style={{ flexGrow: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                        {props.addFormContent}
                    </div>
                    <TablePagination
                        rowsPerPageOptions={[10, 25, 100]}
                        component="span"
                        count={rows.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </TableFooter> :
                    <TablePagination
                        rowsPerPageOptions={[10, 25, 100]}
                        component="span"
                        count={rows.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />}
            </Paper>}
        </>
    );
}
